namespace Nabu.Network;public record PathSet(    string Home,    string Storage,    string Cache,    string Programs,    string Logs,    string Packages,    string StorageSource,    string ClientPrefix){    public string SettingsPath => Path.Combine(Home, StorageNames.SettingsFileName);    public static PathSet FromHome()    {        var root = Environment.GetFolderPath(Environment.SpecialFolder.UserProfile);        var home = Path.Combine(root, StorageNames.Home);        var storage = Path.Combine(home, StorageNames.Storage);        return new PathSet(            home,            storage,            Path.Combine(home, StorageNames.Cache),            Path.Combine(home, StorageNames.Programs),            Path.Combine(home, StorageNames.Logs),            Path.Combine(home, StorageNames.Packages),            Path.Combine(storage, StorageNames.StorageSource),            StorageNames.ClientPrefix        );    }    }