namespace Gry.Workers;public record SlidingTimeSpan{    private TimeSpan InitialValue { get; }    private TimeSpan Value { get; set; }    public TimeSpan Increment { get; }    public TimeSpan Max { get; }    public SlidingTimeSpan(TimeSpan value, TimeSpan increment, TimeSpan max)    {        InitialValue = Value = value;        Increment = increment;        Max = max;    }    public void Reset()    {        Value = InitialValue;    }    public static SlidingTimeSpan From(        double start,         double increment,         double max    )    {        return new(            TimeSpan.FromMilliseconds(start),            TimeSpan.FromMilliseconds(increment),            TimeSpan.FromMilliseconds(max)        );    }    public static implicit operator TimeSpan(SlidingTimeSpan span)    {        var current = span.Value;                span.Value = span.Value.Add(span.Increment);        if (span.Value > span.Max) span.Value = span.Max;                return current;    }}